    public function template_rendar($data = array()) {
        if (!function_exists('is_plugin_active')) {
            include_once( ABSPATH . 'wp-admin/includes/plugin.php' );
    }
        
    $alert = '';
      if (!defined('FLUENTFORM')) {
            echo "
                 <style>
                 .oxi-fluent-form{
                       color: #8a6d3b;
                       background-color: #fcf8e3;
                       border-color: #f9f0c3;
                       padding: 15px;
                       border-left: 5px solid transparent;
                       position: relative;
                       font-size: 12px;
                       line-height: 1.5;
                       text-align: left;
                       font-size: 19px;
                       text-align: center;
                    }    
                 </style>";
            $alert = '<div class="oxi-fluent-form"><strong>Fluent Form</strong> is not installed/activated on your site. Please install and activate <a href="plugin-install.php?s=fluentform&tab=search&type=term" target="_blank">Fluent Form</a> first </div>';
        }
        return __('<div class="oxi-addons-col-1" id="' . $data['style']['style_name'] . '">
                    <div class="oxi-addons-style-preview">
                    '.$alert.'
                        <div class="oxi-addons-style-preview-top oxi-addons-center" style="display: flex; justify-content: center;">
                            <img style="width: 600px;" src="' . $this->image()[$data['style']['style_name']] . '">
                         </div>
                        <div class="oxi-addons-style-preview-bottom">
                            <div class="oxi-addons-style-preview-bottom-left">
                                ' . $this->ShortcodeName($data['style']['style_name']) . '
